const response = $input.item.json;
let dadosProcessados;

try {
  console.log('### ==> ', response)
  // Tentar parsear a resposta da AI
  if (typeof response.choices?.[0]?.message?.content === 'string') {
    dadosProcessados = JSON.parse(response.choices[0].message.content);
  } else if (typeof response.output === 'string') {
    const jsonSplit = response.output.split('```json')
    console.log('### ==> ', jsonSplit)
    dadosProcessados = JSON.parse(jsonSplit[jsonSplit.length-1].replace('```', ''));
  } else if (typeof response === 'object' && response.dados) {
    dadosProcessados = response;
  } else {
    throw new Error('Formato de resposta inv√°lido');
  }

  // Validar estrutura
  if (!dadosProcessados.dados || !Array.isArray(dadosProcessados.dados)) {
    throw new Error('Dados n√£o encontrados ou formato inv√°lido');
  }

  console.log(`Lote ${dadosProcessados.lote}: ${dadosProcessados.dados.length} registros processados`);

  return {
    json: {
      ...dadosProcessados,
      status_processamento: 'sucesso',
      timestamp: new Date().toISOString()
    }
  };

} catch (error) {
  console.log(`Erro no lote: ${error.message}`);

  return [{
    json: {
      lote: $('Split em Lotes').item.json.lote_numero || 0,
      dados: [],
      processados: 0,
      status_processamento: 'erro',
      erro: error.message,
      timestamp: new Date().toISOString()
