  }];
}

// Configura√ß√µes
const BATCH_SIZE = 200; // Ajuste conforme necess√°rio 
const competencia = $input.first().json.competencia || '05.2025';

// Pegar dados do Supabase
const dados = $input.all();
console.log(`Total de registros recebidos: ${dados.length}`);

// Dividir em lotes
const lotes = [];
for (let i = 0; i < dados.length; i += BATCH_SIZE) {
  const lote = {
    lote_numero: Math.floor(i / BATCH_SIZE) + 1,
    total_lotes: Math.ceil(dados.length / BATCH_SIZE),
    registros: dados.slice(i, i + BATCH_SIZE),
    competencia: competencia,
    total_registros_originais: dados.length
  };

  lotes.push(lote);
  console.log(`Lote ${lote.lote_numero} criado com ${lote.registros.length} registros`);
}

// Retornar cada lote como item separado
return lotes.map(lote => ({
  json: lote
}));

// Coletar TODOS os lotes processados (agora funcionar√° corretamente)
const todosLotes = $input.all();
console.log(`üìä Iniciando merge de ${todosLotes.length} lotes`);

let dadosFinais = [];
let totalProcessados = 0;
let totalComErro = 0;
let lotesDetalhados = [];

// Consolidar dados de todos os lotes
for (let i = 0; i < todosLotes.length; i++) {
  const loteItem = todosLotes[i];
  const loteData = loteItem.json;

  console.log(`üì¶ Processando lote ${i + 1}: Status = ${loteData.status_processamento}`);

  // Salvar detalhes do lote para debug
  lotesDetalhados.push({
    lote_numero: loteData.lote || (i + 1),
    status: loteData.status_processamento,
    registros_processados: loteData.dados?.length || 0,
    erro: loteData.erro || null
  });

  if (loteData.status_processamento === 'sucesso' && loteData.dados && Array.isArray(loteData.dados)) {
    loteData.dados.map(item => {
      dadosFinais.push({
          'Matricula': item.Matricula || '',
          'Admissao': item.Data_admissao || '',
          'Sindicato': item.Sindicato || '',
          'Competencia': item.Competencia || '',
          'Dias': item.Dias_uteis || '22',
          'ValorDiarioVR': item.Valor_diario_VR || 0.0,
          'Total': item.Total || 0.0,
          'CustoEmpresa': item.Custo_empresa || 0.0,
          'DescontoProfissional': item.Desconto_colaborador || 0.0,
          'ObservacaoGeral': item.Observacao
      })
    })
    // dadosFinais = dadosFinais.concat(loteData.dados);
    totalProcessados += loteData.processados || loteData.dados.length;
    console.log(`‚úÖ Lote ${loteData.lote}: ${loteData.dados.length} registros adicionados`);
  } else {
    totalComErro++;
    console.error(`‚ùå Lote ${loteData.lote} com erro: ${loteData.erro}`);
  }
}

// Calcular resumos financeiros
const valorTotal = dadosFinais.reduce((sum, item) => sum + (parseFloat(item.Total) || 0), 0);
const custoEmpresa = dadosFinais.reduce((sum, item) => sum + (parseFloat(item.Custo_empresa) || 0), 0);
const descontoColaborador = dadosFinais.reduce((sum, item) => sum + (parseFloat(item.Desconto_colaborador) || 0), 0);

// Resultado final consolidado
const resultado = {
  dados: dadosFinais,
  resumo: {
    total_colaboradores: dadosFinais.length,
    total_processados: totalProcessados,
    total_lotes: todosLotes.length,
    lotes_sucesso: todosLotes.length - totalComErro,
    lotes_com_erro: totalComErro,
    valor_total_geral: parseFloat(valorTotal.toFixed(2)),
    custo_total_empresa: parseFloat(custoEmpresa.toFixed(2)),
    desconto_total_colaborador: parseFloat(descontoColaborador.toFixed(2))
  },
  metadata: {
    competencia: todosLotes[0]?.json?.competencia || '',
    processamento_completo: totalComErro === 0,
    timestamp: new Date().toISOString(),
    lotes_detalhados: lotesDetalhados
  }
};

console.log(`üéâ Processamento conclu√≠do: ${dadosFinais.length} registros finais de ${todosLotes.length} lotes`);
console.log(`üí∞ Valor total: R$ ${valorTotal.toFixed(2)}`);

return [{ json: resultado }];

