// Validar se temos dados
if (!dados || !Array.isArray(dados) || dados.length === 0) {
    console.error('‚ùå Nenhum dado encontrado para gerar planilha');
    return [{
        json: {
            error: true,
            message: 'Nenhum dado encontrado para gerar planilha',
            recordCount: 0
        }
    }];
}

// =============================================================================
// PREPARAR DADOS PARA CSV/XLSX
// =============================================================================

let csvContent = '';

// Headers da planilha
const headers = [
    'Matr√≠cula',
    'Admiss√£o', 
    'Sindicato do Colaborador',
    'Compet√™ncia',
    'Dias',
    'Valor Di√°rio VR',
    'Total',
    'Custo Empresa',
    'Desconto Profissional',
    'Observa√ß√£o Geral'
];

// Adicionar headers ao CSV
csvContent += headers.join(',') + '\n';

// Processar cada linha de dados
let linhasProcessadas = 0;
dados.forEach((item, index) => {
    try {
        // Extrair dados com fallback para diferentes formatos de campo
        const matricula = item.Matricula || item.matricula || '';
        const dataAdmissao = item.Admissao || item["Data admissao"] || item.data_admissao || '';
        const sindicato = item.Sindicato || item["Sindicato do colaborador"] || item.sindicato || '';
        const competencia = item.Competencia || item["Compet√™ncia"] || item.competencia || resultado.metadata.competencia || '';
        const diasUteis = item.Dias || item["Dias √∫teis"] || item.dias_uteis || 0;
        const valorDiario = parseFloat(item.ValorDiarioVR || item["Valor di√°rio VR"] || item.valor_vr_dia || 0).toFixed(2);
        const total = parseFloat(item.Total || item.total || 0).toFixed(2);
        const custoEmpresa = parseFloat(item.CustoEmpresa || item["Custo empresa"] || item.custo_empresa || 0).toFixed(2);
        const descontoColaborador = parseFloat(item.DescontoProfissional || item["Desconto colaborador"] || item.desconto_colaborador || 0).toFixed(2);
        const observacao = item.ObservacaoGeral || item["Observacao geral"] || item.Observacao_geral || item.observacao || '';

        // Criar linha CSV (escapando aspas duplas)
        const row = [
            `"${String(matricula).replace(/"/g, '""')}"`,
            `"${String(dataAdmissao).replace(/"/g, '""')}"`,
            `"${String(sindicato).replace(/"/g, '""')}"`,
            `"${String(competencia).replace(/"/g, '""')}"`,
            `"${diasUteis}"`,
            `"${valorDiario}"`,
            `"${total}"`,
            `"${custoEmpresa}"`,
            `"${descontoColaborador}"`,
            `"${String(observacao).replace(/"/g, '""')}"`
        ];

        csvContent += row.join(',') + '\n';
        linhasProcessadas++;

    } catch (error) {
        console.error(`‚ùå Erro ao processar linha ${index + 1}:`, error.message);
        console.error(`‚ùå Dados da linha:`, JSON.stringify(item, null, 2));
    }
});

// =============================================================================
