const resumo = resultado.resumo || {};
const rowTotais = [
    '"TOTAIS"',
    '""', // Data Admiss√£o vazia
    '""', // Sindicato vazio
    `"${resultado.metadata.competencia || ''}"`,
    '""', // Dias √öteis vazio
    '""', // Valor Di√°rio vazio
    `"${parseFloat(resumo.valor_total_geral || 0).toFixed(2)}"`,
    `"${parseFloat(resumo.custo_total_empresa || 0).toFixed(2)}"`,
    `"${parseFloat(resumo.desconto_total_colaborador || 0).toFixed(2)}"`,
    `"Total: ${resumo.total_colaboradores || linhasProcessadas} colaboradores"`
];

csvContent += rowTotais.join(',') + '\n';

// =============================================================================
// GERAR ARQUIVO
// =============================================================================

// Nome do arquivo com timestamp
const competencia = resultado.dados[0].Competencia || 'XX_XXXX';
const timestamp = new Date().toISOString()
    .slice(0, 16)
    .replace(/[:T-]/g, '_');
const fileName = `VR Mensal ${competencia}.xlsx`;

// Criar buffer com BOM para UTF-8 (garante acentos corretos no Excel)
const csvWithBom = '\ufeff' + csvContent;
const buffer = Buffer.from(csvWithBom, 'utf8');

// Estat√≠sticas finais
const fileStats = {
    totalLinhas: linhasProcessadas + 1, // +1 pela linha de totais
    tamanhoArquivo: buffer.length,
    competencia: competencia,
    valorTotal: resumo.valor_total_geral || 0
};

console.log(`‚úÖ Arquivo gerado com sucesso:`);
console.log(`   üìÑ Nome: ${fileName}`);
console.log(`   üìä Registros: ${linhasProcessadas}`);
console.log(`   üí∞ Valor Total: R$ ${parseFloat(resumo.valor_total_geral || 0).toFixed(2)}`);
console.log(`   üì¶ Tamanho: ${Math.round(buffer.length / 1024)} KB`);

// =============================================================================
// RETORNO FINAL
// =============================================================================

return [{
    json: {
        success: true,
        fileName: fileName,
        recordCount: linhasProcessadas,
        totalAmount: parseFloat(resumo.valor_total_geral || 0).toFixed(2),
        fileSize: buffer.length,
        competencia: competencia,
        timestamp: new Date().toISOString(),
        stats: fileStats,
        metadata: resultado.metadata
    },
    binary: {
        data: buffer,
        mimeType: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        fileName: fileName,
        fileExtension: 'xlsx'
    }
}];

